import org.springframework.stereotype.Repository;

@Repository
public class UserDaoImpl implements UserDao &amp;#123;

    @Override
    public void print() &amp;#123;
        System.out.println(&amp;quot;Dao层执行结束&amp;quot;);
    &amp;#125;
&amp;#125;
&lt;/code&gt;&lt;/pre&gt;
