        &amp;lt;tx:method name=&amp;quot;delete*&amp;quot; read-only=&amp;quot;false&amp;quot; rollback-for=&amp;quot;java.lang.Exception&amp;quot; propagation=&amp;quot;REQUIRES_NEW&amp;quot;/&amp;gt;
    &amp;lt;/tx:attributes&amp;gt;
&amp;lt;/tx:advice&amp;gt;
