&lt;h3 id=&#34;10-4、实验三：基于方法实现校验&#34;&gt;10.4、实验三：基于方法实现校验&lt;/h3&gt;
&lt;p&gt;&lt;strong&gt;第一步 创建配置类，配置MethodValidationPostProcessor&lt;/strong&gt;&lt;/p&gt;
&lt;pre&gt;&lt;code class=&#34;java&#34;&gt;package com.atguigu.spring6.validation.method3;

import org.springframework.context.annotation.Bean;
import org.springframework.context.annotation.ComponentScan;
import org.springframework.context.annotation.Configuration;
import org.springframework.validation.beanvalidation.LocalValidatorFactoryBean;
import org.springframework.validation.beanvalidation.MethodValidationPostProcessor;

@Configuration
@ComponentScan(&amp;quot;com.atguigu.spring6.validation.method3&amp;quot;)
public class ValidationConfig &amp;#123;

    @Bean
    public MethodValidationPostProcessor validationPostProcessor() &amp;#123;
        return new MethodValidationPostProcessor();
    &amp;#125;
&amp;#125;
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;&lt;strong&gt;第二步 创建实体类，使用注解设置校验规则&lt;/strong&gt;&lt;/p&gt;
&lt;pre&gt;&lt;code class=&#34;java&#34;&gt;package com.atguigu.spring6.validation.method3;

import jakarta.validation.constraints.*;

public class User &amp;#123;

    @NotNull
    private String name;

    @Min(0)
    @Max(120)
    private int age;

    @Pattern(regexp = &amp;quot;^1(3|4|5|7|8)\\d&amp;#123;9&amp;#125;$&amp;quot;,message = &amp;quot;手机号码格式错误&amp;quot;)
    @NotBlank(message = &amp;quot;手机号码不能为空&amp;quot;)
    private String phone;

    public String getName() &amp;#123;
        return name;
    &amp;#125;
    public void setName(String name) &amp;#123;
        this.name = name;
    &amp;#125;
    public int getAge() &amp;#123;
        return age;
    &amp;#125;
    public void setAge(int age) &amp;#123;
        this.age = age;
    &amp;#125;
    public String getPhone() &amp;#123;
        return phone;
    &amp;#125;
    public void setPhone(String phone) &amp;#123;
        this.phone = phone;
    &amp;#125;
&amp;#125;
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;&lt;strong&gt;第三步 定义Service类，通过注解操作对象&lt;/strong&gt;&lt;/p&gt;
